let bannerHomeChangeSlide = function (banner, change, slideBlock) {
    let sliderItems = banner.find('.banner-home__slider-item');
    let sliderItemsCount = sliderItems.length;
    let currentItem = banner.find('.banner-home__slider-item.active');
    let currentItemText = banner.find('.banner-home__content-text-item.active');
    let currentItemPosition = parseInt(currentItem.attr('data-position'));
    let newItemPosition;
    let nextItem;
    let nextItemText;

    // Блокируем перелистываение слайдера, если еще не завершена предыдущая операция
    let bannerBlocked = banner.attr('slideBlock');
    if (bannerBlocked === "true") {
        return;
    }
    banner.attr('slideBlock', 'true');

    // выбор операции со слайдером
    switch (change) {
        case "next":
            newItemPosition = currentItemPosition + 1;
            if (newItemPosition > sliderItemsCount) newItemPosition = 1;
            nextItem = banner.find('.banner-home__slider-item[data-position="' + newItemPosition + '"]');
            nextItemText = banner.find('.banner-home__content-text-item[data-position="' + newItemPosition + '"]');
            currentItem.addClass('active-next');
            nextItem.addClass('next');
            currentItemText.addClass('active-next');
            nextItemText.addClass('next');
            setBannerTextItemHeight(nextItemText);

            // передвигаем кружок =)
            banner.find('.banner-home__content').toggleClass('decor-reverse');

            setTimeout(function () {
                currentItem.removeClass('active-next').removeClass('active');
                nextItem.removeClass('next').addClass('active');
                currentItemText.removeClass('active-next').removeClass('active');
                nextItemText.removeClass('next').addClass('active');
                banner.attr('slideBlock', 'false');
            }, 500);
            break;
        case "prev":
            newItemPosition = currentItemPosition - 1;
            if (newItemPosition < 1) newItemPosition = sliderItemsCount;
            nextItem = banner.find('.banner-home__slider-item[data-position="' + newItemPosition + '"]');
            nextItemText = banner.find('.banner-home__content-text-item[data-position="' + newItemPosition + '"]');
            currentItem.addClass('active-next-reverse');
            nextItem.addClass('next-reverse');
            currentItemText.addClass('active-next-reverse');
            nextItemText.addClass('next-reverse');
            setBannerTextItemHeight(nextItemText);

            // передвигаем кружок =)
            banner.find('.banner-home__content').toggleClass('decor-reverse');

            setTimeout(function () {
                currentItem.removeClass('active-next-reverse').removeClass('active');
                nextItem.removeClass('next-reverse').addClass('active');
                currentItemText.removeClass('active-next-reverse').removeClass('active');
                nextItemText.removeClass('next-reverse').addClass('active');
                banner.attr('slideBlock', 'false');
            }, 500);
            break;
        default:
            console.log("params error in function bannerChangeSlide()");
            banner.attr('slideBlock', 'false');
            break;
    }
}

