let menuDropdown;
// на случай ajax подгрузки
let initMenuVariables = function () {
    menuDropdown = $('.menu-dropdown');
}

let menuDropdownHide = function () {
    menuDropdown.removeClass('active');
}

$(function () {
    initMenuVariables();

    $('.burger').on('click', function () {
        $('.menu-dropdown').toggleClass('active');
    });

    $(document).on('click', function (e) {
        let div = $(".menu-dropdown, .burger"); // тут указываем ID элемента
        if (!div.is(e.target) // если клик был не по нашему блоку
            && div.has(e.target).length === 0) { // и не по его дочерним элементам
            if (menuDropdown.hasClass('active')) {
                menuDropdownHide();
            }
        }
    })
});

