$(function () {
    let cookies = $('.notification--cookies');
    // если клиент не принимал условия использования cookie - показываем уведомление через 2s на сайте
    if (Cookies.get('cookie-accept') !== 'true') {
        setTimeout(function () {
            notificationShow(cookies);
        }, 2000);
    }
    // Скрываем уведомление о cookie и сохраняем запись о согласии
    $('#cookies-close').on('click', function (e) {
        e.preventDefault();
        Cookies.set('cookie-accept', 'true');
        notificationHide($(this).closest('.notification'));
    });
    // Скрываем уведомление
    $('.warning-close').on('click', function (e) {
        e.preventDefault();
        notificationHide($(this).closest('.notification'));
    });
    // Вспомогательная кнопка для uikit
    $('#notification-warning-open').on('click', function (e) {
        e.preventDefault();
        warningShot('Проверьте правильность заполнения формы');
    });
    // Вспомогательная кнопка для uikit
    $('#notification-cookies-open').on('click', function (e) {
        e.preventDefault();
        notificationShow(cookies);
    });
})

